-- ----------------------------
-- Function structure for fn_get_hot_keywords
-- ----------------------------
DROP FUNCTION IF EXISTS `fn_get_hot_keywords`;
delimiter ;;
CREATE FUNCTION `fn_get_hot_keywords`(p_limit INT)
 RETURNS varchar(1000) CHARSET utf8mb4
  DETERMINISTIC
BEGIN
    DECLARE result VARCHAR(1000);
    
    SELECT GROUP_CONCAT(keyword SEPARATOR ', ') INTO result
    FROM (
        SELECT keyword
        FROM sys_search_log
        ORDER BY search_count DESC, last_searched_at DESC
        LIMIT p_limit
    ) AS tmp;
    
    RETURN result;
END
;;
delimiter ;

-- ----------------------------
-- Procedure structure for sp_get_user_learning_stats
-- ----------------------------
DROP PROCEDURE IF EXISTS `sp_get_user_learning_stats`;
delimiter ;;
CREATE PROCEDURE `sp_get_user_learning_stats`(IN p_user_id INT)
BEGIN
    SELECT 
        lp.id as path_id,
        lp.title as path_title,
        lp.total_courses,
        COUNT(DISTINCT sp.article_id) as completed_courses,
        ROUND(
            COUNT(DISTINCT sp.article_id) * 100.0 / 
            GREATEST(lp.total_courses, 1)
        , 0) as progress_percent
    FROM sys_learning_path lp
    LEFT JOIN sys_path_course pc ON lp.id = pc.path_id
    LEFT JOIN sys_learning_progress sp ON lp.id = sp.path_id 
        AND pc.article_id = sp.article_id 
        AND sp.user_id = p_user_id
        AND sp.is_completed = 1
    WHERE lp.is_active = 1
    GROUP BY lp.id, lp.title, lp.total_courses
    ORDER BY lp.sort_order;
END
;;
delimiter ;

