DROP PROCEDURE IF EXISTS `sp_increment_article_view`;
delimiter ;;
CREATE PROCEDURE `sp_increment_article_view`(IN p_article_id BIGINT)
BEGIN
    UPDATE sys_article 
    SET quantity = quantity + 1,
        update_time = NOW()
    WHERE id = p_article_id;
END
;;
